import React from 'react';
import jobList from '../../../utils/main/section-experience/jobList';
import tick from '../../../assets/tick.svg';

function Jobs() {
  const jobCards = jobList.map((item) => (
    item.index % 2 !== 0
      ? (
        <li key={item.index} className="job-card">
          <article className="card-left">
            <h2>{item.title}</h2>
            <div className="job-place">
              <h3>{item.location}</h3>
              <h3>{item.date}</h3>
              <ul className="task-list">
                <li className="task-item">
                  <img src={tick} alt="tick symbol" />
                  {item.tasks.one}
                </li>
                <li className="task-item">
                  <img src={tick} alt="tick symbol" />
                  {item.tasks.two}
                </li>
                <li className="task-item">
                  <img src={tick} alt="tick symbol" />
                  {item.tasks.three}
                </li>
                <li className="task-item">
                  <img src={tick} alt="tick symbol" />
                  {item.tasks.four}
                </li>
              </ul>
            </div>
          </article>
          <img src={item.imageUrl} alt={item.imageAlt} className="card-right" />
        </li>
      )
      : (
        <li key={item.index} className="job-card">
          <img src={item.imageUrl} alt={item.imageAlt} className="card-right" />
          <article className="card-left">
            <div>
              <h2>{item.title}</h2>
              <div className="job-place">
                <h3>{item.location}</h3>
                <h3>{item.date}</h3>
              </div>
              <ul className="task-list">
                <li className="task-item">
                  <img src={tick} alt="tick symbol" />
                  {item.tasks.one}
                </li>
                <li className="task-item">
                  <img src={tick} alt="tick symbol" />
                  {item.tasks.two}
                </li>
                <li className="task-item">
                  <img src={tick} alt="tick symbol" />
                  {item.tasks.three}
                </li>
                <li className="task-item">
                  <img src={tick} alt="tick symbol" />
                  {item.tasks.four}
                </li>
              </ul>
            </div>
          </article>
        </li>
      )
  ));

  return (
    <ul className="job-card-list">
      {jobCards}
    </ul>
  );
}

export default Jobs;
